const e=JSON.parse('{"key":"v-766e6a0c","path":"/posts/Devops/6%E3%80%81%E8%AF%BB%E5%90%8E%E6%84%9F%E2%80%94%E2%80%94%E6%8E%A2%E7%B4%A2%E9%AA%8C%E8%AF%818%E5%AD%97%E7%8E%AF.html","title":"6、持续交付中的探索验证环","lang":"en-US","frontmatter":{"icon":"pen-to-square","date":"2025-03-02T00:00:00.000Z","category":["devops"],"tag":["devops"],"star":true,"description":"6、持续交付中的探索验证环 一、什么是持续交付 《持续交付2.0》是一本由乔梁所著的书籍，它深入探讨了如何在现代软件开发环境中实施持续交付。 持续交付是建立在“可持续地快速发布软件服务”和“最小化可行产品”两种理念基础之上，强调要以业务为导向，从一开始就将业务问题进行分解，并通过不断的科学探索与快速验证，减少浪费的同时，快速找到正确的业务前进方向。其涉及组织中的多个团队，需要各个团队之间紧密合作。 “持续交付”的4个核心工作原则是坚持少做、持续分解问题、坚持快速反馈和持续改进并衡量。只有这样，才能不断缩短持续交付“8”字环的运行周期，提升用户反馈速度，从而提高业务的敏捷性。这要求管理者跳出原有软件交付管理思维模式，摆脱“害怕失败”的恐惧感，拥抱“科学探索—快速验证”思维方法，快速试错，提升持续交付能力，进而发展现有业务，并快速开创新业务。","head":[["meta",{"property":"og:url","content":"https://y-aong.github.io/orderlines_blog/orderlines_blog/posts/Devops/6%E3%80%81%E8%AF%BB%E5%90%8E%E6%84%9F%E2%80%94%E2%80%94%E6%8E%A2%E7%B4%A2%E9%AA%8C%E8%AF%818%E5%AD%97%E7%8E%AF.html"}],["meta",{"property":"og:site_name","content":"ORDERLINES"}],["meta",{"property":"og:title","content":"6、持续交付中的探索验证环"}],["meta",{"property":"og:description","content":"6、持续交付中的探索验证环 一、什么是持续交付 《持续交付2.0》是一本由乔梁所著的书籍，它深入探讨了如何在现代软件开发环境中实施持续交付。 持续交付是建立在“可持续地快速发布软件服务”和“最小化可行产品”两种理念基础之上，强调要以业务为导向，从一开始就将业务问题进行分解，并通过不断的科学探索与快速验证，减少浪费的同时，快速找到正确的业务前进方向。其涉及组织中的多个团队，需要各个团队之间紧密合作。 “持续交付”的4个核心工作原则是坚持少做、持续分解问题、坚持快速反馈和持续改进并衡量。只有这样，才能不断缩短持续交付“8”字环的运行周期，提升用户反馈速度，从而提高业务的敏捷性。这要求管理者跳出原有软件交付管理思维模式，摆脱“害怕失败”的恐惧感，拥抱“科学探索—快速验证”思维方法，快速试错，提升持续交付能力，进而发展现有业务，并快速开创新业务。"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"en-US"}],["meta",{"property":"og:updated_time","content":"2025-03-17T09:50:53.000Z"}],["meta",{"property":"article:author","content":"Y-aong"}],["meta",{"property":"article:tag","content":"devops"}],["meta",{"property":"article:published_time","content":"2025-03-02T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2025-03-17T09:50:53.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"6、持续交付中的探索验证环\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2025-03-02T00:00:00.000Z\\",\\"dateModified\\":\\"2025-03-17T09:50:53.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"Y-aong\\",\\"url\\":\\"https://github.com/Y-aong\\"}]}"]]},"headers":[{"level":3,"title":"一、什么是持续交付","slug":"一、什么是持续交付","link":"#一、什么是持续交付","children":[]},{"level":3,"title":"二、探索环和验证环","slug":"二、探索环和验证环","link":"#二、探索环和验证环","children":[]},{"level":3,"title":"三、关于验证环四个工作原则的思考","slug":"三、关于验证环四个工作原则的思考","link":"#三、关于验证环四个工作原则的思考","children":[]}],"git":{"createdTime":1742205053000,"updatedTime":1742205053000,"contributors":[{"name":"Y-aong","email":"1627469727@qq.com","commits":1}]},"readingTime":{"minutes":6.42,"words":1926},"filePathRelative":"posts/Devops/6、读后感——探索验证8字环.md","localizedDate":"March 2, 2025","excerpt":"<h1> 6、持续交付中的探索验证环</h1>\\n<h3> 一、什么是持续交付</h3>\\n<p>《持续交付2.0》是一本由乔梁所著的书籍，它深入探讨了如何在现代软件开发环境中实施持续交付。</p>\\n<p>持续交付是建立在“可持续地快速发布软件服务”和“最小化可行产品”两种理念基础之上，强调要以业务为导向，从一开始就将业务问题进行分解，并通过不断的科学探索与快速验证，减少浪费的同时，快速找到正确的业务前进方向。其涉及组织中的多个团队，需要各个团队之间紧密合作。</p>\\n<p>“持续交付”的4个核心工作原则是坚持少做、持续分解问题、坚持快速反馈和持续改进并衡量。只有这样，才能不断缩短持续交付“8”字环的运行周期，提升用户反馈速度，从而提高业务的敏捷性。这要求管理者跳出原有软件交付管理思维模式，摆脱“害怕失败”的恐惧感，拥抱“科学探索—快速验证”思维方法，快速试错，提升持续交付能力，进而发展现有业务，并快速开创新业务。</p>","autoDesc":true}');export{e as data};
